import React, { useEffect, useState } from 'react';
import { getProductos, addProducto, updateProducto, deleteProducto } from '../services/productosService';

const ProductosTable = () => {
    const [productos, setProductos] = useState([]);
    const [producto, setProducto] = useState({ nombre: '', codigo: '', inventario: '', marca: '', valor: '' });
    const [productoIdParaEditar, setProductoIdParaEditar] = useState(null);

    useEffect(() => {
        fetchProductos();
    }, []);

    const fetchProductos = async () => {
        const { data } = await getProductos();
        setProductos(data);
    };

    const handleSave = async () => {
        if (productoIdParaEditar) {
            await updateProducto(productoIdParaEditar, producto);
            setProductoIdParaEditar(null);
        } else {
            await addProducto(producto);
        }
        fetchProductos();
        setProducto({ nombre: '', codigo: '', inventario: '', marca: '', valor: '' });
    };

    const handleDelete = async (id) => {
        await deleteProducto(id);
        fetchProductos();
    };

    const cargarProductoParaEditar = (prod) => {
        setProductoIdParaEditar(prod.id);
        setProducto({
            nombre: prod.nombre,
            codigo: prod.codigo,
            inventario: prod.inventario,
            marca: prod.marca,
            valor: prod.valor
        });
    };

    return (
        <div>
            <h1>Productos</h1>
            <table>
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Codigo</th>
                        <th>Código</th>
                        <th>Inventario</th>
                        <th>Marca</th>
                        <th>Valor</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {productos.map((prod) => (
                        <tr key={prod.id}>
                            <td>{prod.id}</td>
                            <td>{prod.nombre}</td>
                            <td>{prod.codigo}</td>
                            <td>{prod.inventario}</td>
                            <td>{prod.marca}</td>
                            <td>{prod.valor}</td>
                            <td>
                                <button onClick={() => cargarProductoParaEditar(prod)}>Editar</button>
                                <button onClick={() => handleDelete(prod.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Formulario para añadir/editar producto */}
            <div>
                <input placeholder="Nombre" value={producto.nombre} onChange={(e) => setProducto({ ...producto, nombre: e.target.value })} />
                <input placeholder="Código" value={producto.codigo} onChange={(e) => setProducto({ ...producto, codigo: e.target.value })} />
                <input placeholder="Inventario" value={producto.inventario} onChange={(e) => setProducto({ ...producto, inventario: e.target.value })} />
                <input placeholder="Marca" value={producto.marca} onChange={(e) => setProducto({ ...producto, marca: e.target.value })} />
                <input placeholder="Valor" value={producto.valor} onChange={(e) => setProducto({ ...producto, valor: e.target.value })} />
                <button onClick={handleSave}>{productoIdParaEditar ? 'Actualizar Producto' : 'Añadir Producto'}</button>
            </div>
        </div>
    );
};

export default ProductosTable;
