import React, { useEffect, useState } from 'react';
import { getProductos, addProducto, updateProducto, deleteProducto } from '../services/productosService';

const ProductosTable = () => {
    const [productos, setProductos] = useState([]);
    const [producto, setProducto] = useState({ codigo: '', inventario: '', marca: '', valor: '', estado: '' });

    useEffect(() => {
        fetchProductos();
    }, []);

    const fetchProductos = async () => {
        const { data } = await getProductos();
        setProductos(data);
    };

    const handleAdd = async () => {
        await addProducto(producto);
        fetchProductos();
    };

    const handleUpdate = async (id) => {
        await updateProducto(id, producto);
        fetchProductos();
    };

    const handleDelete = async (id) => {
        await deleteProducto(id);
        fetchProductos();
    };

    const [productoIdParaEditar, setProductoIdParaEditar] = useState(null);


    return (
        <div>
            <h1>Productos</h1>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Código</th>
                        <th>Inventario</th>
                        <th>Marca</th>
                        <th>Valor</th>
                        <th>Estado</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {productos.map((prod) => (
                        <tr key={prod.id}>
                            <td>{prod.id}</td>
                            <td>{prod.codigo}</td>
                            <td>{prod.inventario}</td>
                            <td>{prod.marca}</td>
                            <td>{prod.valor}</td>
                            <td>{prod.estado}</td>
                            <td>
                                <button onClick={() => handleUpdate(prod.id)}>Editar</button>
                                <button onClick={() => handleDelete(prod.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Formulario para añadir producto */}
            <div>
                <input placeholder="Código" onChange={(e) => setProducto({ ...producto, codigo: e.target.value })} />
                <input placeholder="Inventario" onChange={(e) => setProducto({ ...producto, inventario: e.target.value })} />
                <input placeholder="Marca" onChange={(e) => setProducto({ ...producto, marca: e.target.value })} />
                <input placeholder="Valor" onChange={(e) => setProducto({ ...producto, valor: e.target.value })} />
                <input placeholder="Estado" onChange={(e) => setProducto({ ...producto, estado: e.target.value })} />
                <button onClick={handleAdd}>Añadir Producto</button>
            </div>
        </div>
    );
};

export default ProductosTable;
